package easycoin;/** * * @author  werni */public class Splasher {    /**     * Shows the splash screen, launches the application and then disposes     * the splash screen.     * @param args the command line arguments     */    public static void main(String[] args) {        SplashWindow.splash(Splasher.class.getResource("EasyCoin.jpg"));        SplashWindow.invokeMain("easycoin.executor.Main", args);        SplashWindow.disposeSplash();    }    }